import React from 'react'
import { connect } from 'react-redux'

function OrderList(props) {
    return (
        <div className="bg-light p-5" >
            <h4>Total Orders - { props.orders.length }</h4>
            <ul class="nav nav-tabs pt-2" id="myTab" role="tablist">
                <li class="nav-item">
                    <a class="nav-link active" id="home-tab" data-toggle="tab" href="#home" role="tab" aria-controls="home" aria-selected="true">Pending</a>
                </li>
                <li class="nav-item">
                    <a class="nav-link" id="profile-tab" data-toggle="tab" href="#profile" role="tab" aria-controls="profile" aria-selected="false">Completed</a>
                </li>
            </ul>
            <div class="tab-content" id="myTabContent">
                <div class="tab-pane fade show active" id="home" role="tabpanel" aria-labelledby="home-tab">
                    <ul className="list-unstyled">
                        {
                            props.orders.map(order => {
                                return <li className="bg-secondary" >order.name</li>       
                            })
                        }
                    </ul>
                </div>

                <div class="tab-pane fade" id="profile" role="tabpanel" aria-labelledby="profile-tab">

                </div>
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        orders: state.orders
    }
}

export default connect(mapStateToProps)(OrderList)
