import React from 'react';
import ReactDOM from 'react-dom';
import { Provider } from 'react-redux'
import './index.css';
import App from './App';
import configureStore from './store/configureStore'
import axios from './config/axios'
import { setUser } from './actions/userAction'
import { startSetIncomes, setIncomes} from './actions/incomeAction'
import { startSetExpenses, setExpenses } from './actions/expenseAction'
import { startSetBorrows, setBorrows } from './actions/borrowAction'
import { startSetLends, setLends } from './actions/lendAction'
import { startSetCategories, setCategories } from './actions/categoryAction'

const store = configureStore()

// store.subscribe(() => {
//     console.log(store.getState())
// })

store.dispatch(setUser([]))
store.dispatch(setIncomes([]))
store.dispatch(setExpenses([]))
store.dispatch(setBorrows([]))
store.dispatch(setLends([]))
store.dispatch(setCategories([]))

localStorage.getItem('x-auth') && (
    axios.get('/user', {
        headers: {
            'x-auth': localStorage.getItem('x-auth')
        }
    })
        .then((response) => {
            const user = response.data
            if(user) {
                store.dispatch(setUser(user))
                store.dispatch(startSetIncomes())
                store.dispatch(startSetExpenses())
                store.dispatch(startSetBorrows())
                store.dispatch(startSetLends())
                store.dispatch(startSetCategories())
            }
        })
)

const element = (
    <Provider store={store}>
        <App />
    </Provider>
)

ReactDOM.render(element, document.getElementById('root'));
