import React from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

class SinglePost extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            user: {},
            post: {},
            comments: []
        }
    }
    componentDidMount(){
        axios.get(`https://jsonplaceholder.typicode.com/posts/${this.props.match.params.id}`)
        .then(response => {
            const post = response.data
            axios.get(`https://jsonplaceholder.typicode.com/users/${post.userId}`)
            .then(response => {
                const user = response.data
                this.setState({user})
            })
            this.setState({post})
        })
        axios.get(`https://jsonplaceholder.typicode.com/comments?postId=${this.props.match.params.id}`)
        .then(response => {
            const comments = response.data
            this.setState({comments})
        })
    }
    render(){
        return (
            <div>
                <h2>USER NAME: {this.state.user.name}</h2>
                <h2>TITLE: {this.state.post.title}</h2>
                <h3>BODY: {this.state.post.body}</h3>
                <hr/>
                <h2>COMMENTS</h2>
                <ul>
                    {
                        this.state.comments.map(comment => {
                            return <li key={comment.id}>{comment.body}</li>
                        })
                    }
                </ul>
                <hr/>
                <p><Link to={`/users/${this.state.user.id}`}>More posts of author: {this.state.user.name}</Link></p>
            </div>
        )
    }
}

export default SinglePost
