import React from 'react'

class CustomerEditForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            email: props.order.email || '',
            phone: props.order.phone || ''
        }
    }
    handleSubmit = e => {
        e.preventDefault()
    }
    handleChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    render() {
        const { email, phone } = this.state
        return (
            <div className="container p-5" style={{width: '500px'}}>
                <h4 className="mb-3">Edit Customer Details</h4>
                <hr/>
                <form onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Email address</label>
                        <input type="email" name="email" value={email} onChange={this.handleChange} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email address" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputPhone">Phone</label>
                        <input type="phone" name="phone" value={phone} onChange={this.handleChange} className="form-control" id="exampleInputPhone" placeholder="Enter mobile number" />
                        { !phone ? <p className="text-danger" style={{fontSize: '14px'}} >* Phone number NA</p>: ''}
                    </div>
                    <button type="submit" className="btn btn-primary">Save</button>
                </form>
            </div>
        )
    }
}

export default CustomerEditForm
