import axios from "../config/axios"

const setOrders = (orders) => {
    return {
        type: 'SET_ORDERS',
        payload: orders
    }
}

export const startSetOrders = () => {
    return (dispatch) => {
        axios.get('/orders')
            .then((response) => {
                if(Array.isArray(response.data)) {
                    dispatch(setOrders(response.data))
                }
            })
            .catch((err) => {
                console.log(err)
            })     
    }
}

const addOrder = (order) => {
    return {
        type: 'ADD_ORDER',
        payload: order
    }
}
