import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { FaEdit } from 'react-icons/fa'
import moment from 'moment'

class OrderList extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            newOrder: props.orders.length
        }
    }
    componentWillReceiveProps(nextProps) {
        if(nextProps.orders.length > this.props.orders.length) {
            this.setState({
                newOrder: nextProps.orders.length
            })
        }
    }
    render() {
        console.log(this.state.newOrder, this.props.orders.length)
        return (
            <div className="p-5" >
                <h4 className="pb-2" >Total Orders - { this.props.orders.length }</h4>
                {
                    this.state.newOrder == this.props.orders.length - 1 ? (
                        <div class="alert alert-primary" role="alert">
                            A simple primary alertâ€”check it out!
                        </div>
                    ) : ''
                }
                <table className="table">
                    <thead className="bg-white">
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Order</th>
                            <th scope="col">Date</th>
                            <th scope="col">Customer</th>
                            <th scope="col">Total</th>
                        </tr>
                    </thead>
                    <tbody>
                    {
                        this.props.orders.map((order, index) => {
                            return (
                                    <tr key={order._id}>
                                        <th scope="row">{ index + 1 }</th>
                                        <td>{ order.name }</td>
                                        <td>{ order.created_at } - { moment().startOf('hour').from(order.created_at) } </td>
                                        <td>{ order.first_name + ' ' + order.last_name }
                                            <Link to={`/customers/${order._id}`}><FaEdit className="ml-2" /></Link>
                                        </td>
                                        <td>{ order.total_price }</td>
                                    </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        orders: state.orders
    }
}

export default connect(mapStateToProps)(OrderList)
