import React from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import { connect } from 'react-redux';

class SingleUser extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            postsByUser: []
        }
    }
    componentDidMount(){
        axios.get(`https://jsonplaceholder.typicode.com/posts?userId=${this.props.match.params.id}`)
        .then(response => {
            const postsByUser = response.data
            this.setState({postsByUser})
        })
    }
    render(){
        return (
            <div>
                <h2>USER NAME: {this.props.user.name}</h2>
                <h3>POSTS WRITTEN BY USER</h3>
                <ul>
                    {
                        this.state.postsByUser.map(post => {
                            return <li key={post.id}><Link to={`/posts/${post.id}`}>{post.title}</Link></li>
                        })
                    }
                </ul>
            </div>
        )
    }
}

const mapStateToProps = (state, props) => {
    const id = props.match.params.id;
    return {
        user: state.users.find(user => user.id == id)
    }
}

export default connect(mapStateToProps)(SingleUser);
