import React from 'react'
import { connect } from 'react-redux'
import { startEditOrder } from '../../actoins/ordersAction'

class CustomerEditForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            first_name: props.order.first_name || '',
            last_name: props.order.last_name || '',
            email: props.order.email || '',
            phone: props.order.phone ? props.order.phone.slice(0, 3) : '',
            code: props.order.phone ? props.order.phone.slice(0,3) : '+91'
        }
    }
    handleSubmit = e => {
        e.preventDefault()
        const { first_name, last_name, email, phone } = this.state
        this.props.dispatch(startEditOrder({ first_name, last_name, email, phone }, this.props))
    }
    handleChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    render() {
        const { first_name, last_name, email, phone, code } = this.state
        return (
            <div className="container p-5" style={{width: '500px'}}>
                <h4 className="mb-3">Edit Customer Details</h4>
                <hr/>
                <form onSubmit={this.handleSubmit}>
                <div className="form-group">
                        <label htmlFor="first_name">First name</label>
                        <input type="text" name="first_name" value={first_name} onChange={this.handleChange} className="form-control" id="first_name" aria-describedby="first_name" placeholder="First name" />
                        { !first_name ? <p className="text-danger mt-2" style={{fontSize: '14px'}} >* First name NA</p>: ''}
                    </div>
                    <div className="form-group">
                        <label htmlFor="last_name">Last name</label>
                        <input type="text" name="last_name" value={last_name} onChange={this.handleChange} className="form-control" id="last_name" aria-describedby="last_name" placeholder="Last name" />
                        { !last_name ? <p className="text-danger mt-2" style={{fontSize: '14px'}} >* Last name NA</p>: ''}
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Email address</label>
                        <input type="email" name="email" value={email} onChange={this.handleChange} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email address" />
                        { !email ? <p className="text-danger mt-2" style={{fontSize: '14px'}} >* Email NA</p>: ''}
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputPhone">Phone</label>
                        <div class="input-group mb-2">
                            <div class="input-group-prepend">
                                <div class="input-group-text">{ code }</div>
                            </div>
                            <input type="phone" name="phone" value={phone} onChange={this.handleChange} className="form-control" id="exampleInputPhone" placeholder="Enter mobile number" />
                        </div>
                        { !phone ? <p className="text-danger mt-2" style={{fontSize: '14px'}} >* Phone number NA</p>: ''}
                    </div>
                    <button type="submit" className="btn btn-primary">Save</button>
                </form>
            </div>
        )
    }
}

export default connect()(CustomerEditForm)
